import axios from 'axios'
import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getUser } from './redux/userSlice'
import { useNavigate } from 'react-router-dom'


const Users = () => {
    const dispatch = useDispatch()
    const users = useSelector(state => state.users.users)
    const navigate = useNavigate()
    // console.log('User Display Selector', useSelector(state => state.users.users))
    console.log('Display Users: ', users)

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_PORT}`);
                console.log('User API', response)
                dispatch(getUser(response.data));
            } catch (error) {
                console.log('fetchData() Error: ', error)
            }
        }
        fetchData();
    }, [])
    return (
        <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
            <div className='w-50 bg-white rounded p-3'>
                <button onClick={()=>navigate} className='btn btn-success btn-sm'>Add +</button>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Age</th>
                            <th>action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => {
                                return (
                                    <tr>
                                        <td>{user.name}</td>
                                        <td>{user.email}</td>
                                        <td>{user.age}</td>
                                        <td>
                                            <button className='btn btn-sm btn-success'>Update</button>
                                            <button className='btn btn-sm btn-danger'>Delete</button>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </div>

        </div>
    )
}

export default Users
